"var lastKPI = data.lastKPI; // Is the _source object
  var lastTimestamp = lastKPI["@timestamp"];
  var lastapiBuilderHostname = lastKPI.meta.apiBuilderHostname;
  var managementKPIsInterval = data.config.managementKPIsInterval;
  var now = Date.now();
  logger.info(`Check timestamp of last KPI: ${lastTimestamp} vs. current time: ${now} and managementKPIsInterval: ${managementKPIsInterval}`);
  if(now - lastTimestamp > managementKPIsInterval * 2) { // If the last KPI document older than twice the interval, consider it as too old.
    logger.info(`Last KPI document is too old, assuming previous API-Builder has stopped indexing data. Taking over indexing data`);
    return true;
  } else {
    logger.info(`Previous API-Builder running on host: ${lastapiBuilderHostname} is still indexing data. Timestamp of last document: ${lastTimestamp}`);
    return false;
  }"
