"var apiManagerOrganizations = data.apiManagerOrganizations;
  var portalName = data.request.portalName;
  var connection = data.request.connection;
  // We have to add required details to all list item before calling the sub-flow
  for (i = 0; i < apiManagerOrganizations.length; i++) {
    var org = apiManagerOrganizations[i];
    org.portalName = portalName; // Used to query the actual KPI
    org.connection = connection; 
  }
  return apiManagerOrganizations;"
