parameters.value:
"// Perhaps there is a region given
  let region = data.params.region;

  let index = 'apigw-traffic-details';
  // Atach the region if given
  if(region) {
    index = `${index}-${region.toLowerCase()}`;
  }
  index = `${index}-*`;

  if(data.config.testElasticIndex) {
    index = data.config.testElasticIndex;
  }

  return index;"

"//only json is supported. 
  //If param format is skipped automatically json is returned

  if(data.params.format != undefined && data.params.format != 'json') {
    throw {
      name: "unknown format exception",
      message: "unknown format"
    };
  }

  return data.params;"

"let elasticQuery = ({
    "bool": {
      "must": [
        {"term": {"correlationId": data.params.correlationID}},
          {"term": {"processInfo.serviceId": data.params.serviceID}}
      ]
    }
  });

  if(data.user.gatewayManager.isAdmin || data.authzConfig.enableUserAuthorization==false) {
    return elasticQuery;
  }
  var orgFilter;
  if(data.user.apiManager.role == "admin") {
    orgFilter = {
      exists: {
        "field" : "transactionSummary.serviceContext"
      }
    };
  } else {
    orgFilter = {
      term: {
        "transactionSummary.serviceContext.apiOrg" : data.user.apiManager.organizationName
      }
    };
  }
  elasticQuery.bool.must.push(orgFilter);

  return elasticQuery;"
