"var result = {};
  var hits = data.elasticsearch.result.body.hits.hits;
  var protocol = data.params.protocol;
  result.processId = "";
  result.data = [];
  hits.map(function(entry) {
    var dataObject = {};
    var _source = entry._source;
    dataObject.correlationId = _source.correlationId;
    dataObject.timestamp = Date.parse(_source['@timestamp']);
    if(_source.serviceContext) {
      dataObject.serviceName = _source.serviceContext.service;
      dataObject.operation = _source.serviceContext.method;
    }
    dataObject.type = protocol;
    switch (protocol) {
      case "http":
        formatHttpFields(dataObject, _source);
        break;
      case "fileTransfer":
        formatFiletransferFields(dataObject, _source);
        break;
    }
    result.data.push(dataObject);
  });

  function formatHttpFields(dataObject, _source) {    
      if(!_source.http.sslSubject) {
        _source.http.sslSubject = "null";
      }
      dataObject.statustext = _source.http.statusText;
      dataObject.method = _source.http.method;
      dataObject.status = _source.http.status;
      dataObject.wafStatus = _source.http.wafStatus;
      dataObject.subject = _source.http.authSubjectId;
      dataObject.sslsubject = _source.http.sslSubject;
      dataObject.localPort = _source.http.localPort;
      dataObject.uri = _source.http.uri;
      dataObject.vhost = _source.http.vhost;
      dataObject.duration = _source.duration;
      dataObject.finalStatus = _source.finalStatus;
      dataObject.bytesReceived = _source.http.bytesReceived;
      dataObject.bytesSent = _source.http.bytesSent;
      dataObject.remoteName = _source.http.remoteName;
      dataObject.remoteAddr = _source.http.remoteAddr;
      dataObject.remotePort = _source.http.remotePort;
      dataObject.localAddr = _source.http.localAddr;
      dataObject.localPort = _source.http.localPort;
      dataObject.leg = 0;
  }

  function formatFiletransferFields(dataObject, _source) {
      dataObject.remoteAddr = _source.fileTransfer.remoteAddr;
      dataObject.uploadFile = _source.fileTransfer.uploadFile;
      dataObject.direction = _source.fileTransfer.direction;
      dataObject.servicetype = _source.fileTransfer.serviceType;
      dataObject.size = _source.fileTransfer.size;
      dataObject.duration = _source.duration;
      dataObject.subject = _source.fileTransfer.authSubjectId;
      dataObject.finalStatus = _source.finalStatus;
  }


  return result;"

"// Perhaps there is a region given
  let region = data.params.region;
  // Overview screen (Traffic-Details table) 
  // should also include the apigw-traffic-errors documents
  let index = 'apigw-traffic-summary';
  // Atach the region if given
  if(region) {
    index = `${index}-${region.toLowerCase()}`;
  }
  index = `${index}-*`;
  // Check, if there is an index name to test configured (this is set by the tests)
  if(data.config.testElasticIndex) {
    index = data.config.testElasticIndex;
    if(region) {
      index = index.replace("%REGION%", region.toLowerCase());
    }
  }
  // Define the protocol default (mainly for tests, as they are not sending the default)
  if(data.params.protocol == undefined) {
    data.params.protocol = 'http';
  }
  return index;"
