"let result = [];

  data.hits.map(function(entry) {
    let traceObj = {};
    let message = entry._source.message;

    // depth is the number of tabs befor start of the message. 
    // Calculated based on count of white spaces divided by 4
    const regexPattern = new RegExp(/(^\s*)\S*/g);
    let leadingSpaces = regexPattern.exec(message)[1];
	let depth = 0;

    if (leadingSpaces !== null){
      depth = Math.round(leadingSpaces.length/4);
    }

    traceObj.level = entry._source.level;
    traceObj.type = 'trace';
    traceObj.time = new Date(entry._source['@timestamp']).getTime();
    traceObj.depth = depth;
    traceObj.text = message.trim();

    result.push(traceObj);

  });

  return result;"

"//only json is supported. 
  //If param format is skipped automatically json is returned
  if(data.params.format != undefined && data.params.format != 'json') {
    throw {
      name: "unknown format exception",
      message: "unknown format"
    };
  }"

"// Perhaps there is a region given
  let region = data.params.region;
  let index = 'apigw-traffic-trace';
  // Atach the region if given
  if(region) {
    index = `${index}-${region.toLowerCase()}`;
  }
  index = `${index}-*`;

  if(data.config.testElasticIndex) {
    index = data.config.testElasticIndex;
  }
  return index;"
