"var result = {};
  result.processId = "";
  result.data = [];
  data.hits.map(function(entry) {
    var dataObject = {};
    var _source = entry._source;
    dataObject.correlationId = _source.correlationId;
    dataObject.timestamp = Date.parse(_source['@timestamp']);
    if(_source.transactionSummary && _source.transactionSummary.serviceContext) {
      dataObject.serviceName = _source.transactionSummary.serviceContext.service;
      dataObject.operation = _source.transactionSummary.serviceContext.method;
    }
    processInitialTransactionElement(dataObject, _source);
    result.data.push(dataObject);
  });

  function processInitialTransactionElement(dataObject, _source) {
    if(_source.transactionElements && _source.transactionElements.leg0) {
      dataObject.statustext = _source.transactionElements.leg0.protocolInfo.http.statusText;
      dataObject.method = _source.transactionElements.leg0.protocolInfo.http.method;
      dataObject.status = _source.transactionElements.leg0.protocolInfo.http.status;
      dataObject.wafStatus = _source.transactionElements.leg0.protocolInfo.http.wafStatus;
      dataObject.subject = _source.transactionElements.leg0.protocolInfo.http.authSubjectId;
      dataObject.localPort = _source.transactionElements.leg0.protocolInfo.http.localPort;
      dataObject.uri = _source.transactionElements.leg0.protocolInfo.http.uri;
      dataObject.duration = _source.transactionElements.leg0.duration;
      dataObject.type = getProtocolType(_source.transactionElements.leg0.protocolInfo);
      dataObject.finalStatus = _source.transactionElements.leg0.finalStatus;
    }
  }

  function getProtocolType(protocolInfo) {
    if(protocolInfo.hasOwnProperty('http')) {
       return 'http';
    }
  }

  return result;"

"var queryFilters = data.queryFilters;
  var elasticQuery = {};
  queryFilters.push( {
    "term": {
      "processInfo.serviceId": data.params.serviceID
    }
  });
  elasticQuery = { 
    bool: {
      must: queryFilters
    }
  };
  return elasticQuery;"

"var queryFilters = data.queryFilters;
  data.params.field.map(function(entry, index) {
    var op = data.params.op;
    var value = data.params.value[index];
    if(entry === 'duration') {
      var filter = {
        range: {
          "transactionElements.leg0.duration": {
            "gte": value
          }
        }
      };
      queryFilters.push(filter);
      return queryFilters;
 	}
  });"

"var queryFilters = data.queryFilters;
  if(data.params.ago) {
    var ago = data.params.ago;
    var diff = convertAgo(ago);
    var filter = {
      range: {
        "@timestamp": {
          gt: diff
        }
      }
    };
    queryFilters.push(filter);
    return queryFilters;
  }

  function convertAgo(ago) {
    var diff = ago.substring(0, ago.length-1);
    var now = new Date();
    if(ago.endsWith('m')) {
      return now.setMinutes(now.getMinutes() - diff);
    } else if(ago.endsWith('h')) {
      return now.setHours(now.getHours() - diff);
    }
    console.log(`ERRRRO ${ago}`);
  }"

"var queryFilters = data.queryFilters;
  data.params.field.map(function(entry, index, field) {
    var op = data.params.op;
    if(entry === 'timestamp') {
      // If timestamp is given, we expect to get a start and end
      var begin = data.params.value[index];
      var end = 0;
      if(field[index+1] === 'timestamp') {
        end = data.params.value[index+1];
      } else {
        return queryFilters;
      }
      console.log(`begin: ${begin}`);
      console.log(`end: ${end}`);
      var filter = {
        range: {
          "@timestamp": {
            "gte": begin, 
            "lte": end
          }
        }
      };
      queryFilters.push(filter);
      return queryFilters;
 	}
  });"

"let index = 'logstash-openlog';
  if(data.config.testElasticIndex) {
    index = data.config.testElasticIndex;
  }
  return index;"

"var queryFilters = [];

  data.fields = [
    // This Object-Array declares for each field how to query it in ES
    { fieldName: 'uri', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.uri' }, 
    { fieldName: 'wafStatus', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.wafStatus' }, 
    { fieldName: 'finalStatus', queryType: 'match', queryLocation: 'transactionElements.leg0.finalStatus' }, 
    { fieldName: 'remotePort', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.remotePort' }, 
    { fieldName: 'remoteName', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.remoteName' }, 
    { fieldName: 'localAddr', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.localAddr' },
    { fieldName: 'status', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.status' },
    { fieldName: 'subject', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.authSubjectId' },
    { fieldName: 'operation', queryType: 'match', queryLocation: 'transactionSummary.serviceContext.method' },
    { fieldName: 'localPort', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.localPort' },
    { fieldName: 'method', queryType: 'match', queryLocation: 'transactionElements.leg0.protocolInfo.http.method' },
    { fieldName: 'correlationId', queryType: 'match', queryLocation: 'correlationId' },
    { fieldName: 'serviceName', queryType: 'match', queryLocation: 'transactionSummary.serviceContext.service' },

  ];

  if(typeof data.params.field == 'undefined') {
    data.params.field = [];
  }
  if(typeof data.params.field == 'string') {
    data.params.field = [data.params.field];
  }
  if(typeof data.params.value === 'undefined') {
    data.params.value = [];
  }
  if(typeof data.params.value == 'string') {
    data.params.value = [data.params.value];
  }
  return queryFilters;"

"var queryFilters = data.queryFilters;
  data.fields.map(function(entry) {
    data.params.field.map(function(fieldEntry, index) {
      if(entry.fieldName === fieldEntry) {
        var queryType = entry.queryType;
        var queryLocation = entry.queryLocation;
        var value = data.params.value[index];
        var filter = {};
        var filterQuery = {};
        filterQuery[queryLocation] = value;
        filter[queryType] =  filterQuery;
        queryFilters.push(filter);
        return queryFilters;
      }
    });
  });"
